mapper.update:
update T_CAR
set CAR_ID = #{carId,jdbcType=VARCHAR},
  CAR_NAME = #{carName,jdbcType=VARCHAR},
  CAR_COUNT = #{carCount,jdbcType=DECIMAL},
  CAR_ALL = #{carAll,jdbcType=DECIMAL}
where ID = #{id,jdbcType=DECIMAL}

mapper.select:
select
count(*)
from (
select * from t_car

	
		AND CAR_ID like '%' || #{carId,jdbcType=VARCHAR} || '%'
	
	

)

		SELECT * FROM
		(
		SELECT A.*, rownum r
		FROM
		(select * from t_car
		
			
				AND CAR_ID like '%' || #{carId,jdbcType=VARCHAR} || '%'
			

		
		)A
		WHERE rownum <= #{end,jdbcType=DECIMAL}
		) B
		WHERE r >#{start,jdbcType=DECIMAL} order by ID
