mapper.insert:
insert into T_IN (ID, IN_ID, IN_NOTE, 
  WM_ID, WM_COUNT, P_ID, 
  P_COUNT, CREATE_TIME, UPDATE_TIME
  )
values (#{id,jdbcType=DECIMAL}, #{inId,jdbcType=VARCHAR}, #{inNote,jdbcType=VARCHAR}, 
  #{wmId,jdbcType=DECIMAL}, #{wmCount,jdbcType=DECIMAL}, #{pId,jdbcType=DECIMAL}, 
  #{pCount,jdbcType=DECIMAL}, #{createTime,jdbcType=DATE}, #{updateTime,jdbcType=DATE}
  )

mapper.select:
select
count(*)
from (
select * from t_in

	
		AND IN_ID like '%' || #{inId,jdbcType=VARCHAR} || '%'
	
	

)

		SELECT * FROM
		(
		SELECT A.*, rownum r
		FROM
		(select * from t_in
		
			
				AND IN_ID like '%' || #{inId,jdbcType=VARCHAR} || '%'
			

		
		)A
		WHERE rownum <= #{end,jdbcType=DECIMAL}
		) B
		WHERE r >#{start,jdbcType=DECIMAL} order by ID
