mapper.update:
update T_POSITION
set p_id = #{pId,jdbcType=DECIMAL},
  p_count = #{pCount,jdbcType=DECIMAL},
  p_all = #{pAll,jdbcType=DECIMAL},
  p_name = #{pName,jdbcType=VARCHAR}
where id = #{id,jdbcType=DECIMAL}

mapper.select:
select
count(*)
from (
select * from t_position

	
		AND P_ID like '%' || #{pId,jdbcType=VARCHAR} || '%'
	
	

)

		SELECT * FROM
		(
		SELECT A.*, rownum r
		FROM
		(select * from t_position
		
			
				AND p_id like '%' || #{pId,jdbcType=VARCHAR} || '%'
			

		
		)A
		WHERE rownum <= #{end,jdbcType=DECIMAL}
		) B
		WHERE r >#{start,jdbcType=DECIMAL} order by ID
