mapper.update:
update T_WM
set wm_id = #{wmId,jdbcType=VARCHAR},
  wm_name = #{wmName,jdbcType=VARCHAR},
  wm_count = #{wmCount,jdbcType=DECIMAL},
  wm_all = #{wmAll,jdbcType=DECIMAL}
where id = #{id,jdbcType=DECIMAL}

mapper.select:
select
count(*)
from (
select * from t_wm

	
		AND WM_ID like '%' || #{wmId,jdbcType=VARCHAR} || '%'
	
	

)

		SELECT * FROM
		(
		SELECT A.*, rownum r
		FROM
		(select * from t_wm
		
			
				AND WM_ID like '%' || #{wmId,jdbcType=VARCHAR} || '%'
			

		
		)A
		WHERE rownum <= #{end,jdbcType=DECIMAL}
		) B
		WHERE r >#{start,jdbcType=DECIMAL} order by B.WM_ID
